{
  "info": {
    "_postman_id": "piggie-rich-backend-api-collection",
    "name": "whaiwhaisweeps Backend API",
    "schema": "https://schema.getpostman.com/json/collection/v2.1.0/collection.json"
  },
  "item": [
    {
      "name": "Register",
      "request": {
        "method": "POST",
        "header": [
          { "key": "Content-Type", "value": "application/json" }
        ],
        "body": {
          "mode": "raw",
          "raw": "{\n  \"username\": \"newuser\",\n  \"password\": \"yourpassword\",\n  \"dob\": \"1990-01-01\",\n  \"firstName\": \"John\",\n  \"lastName\": \"Doe\",\n  \"email\": \"newuser@example.com\",\n  \"phone\": \"1234567890\"\n}"
        },
        "url": {
          "raw": "{{base_url}}/api/register",
          "host": ["{{base_url}}"],
          "path": ["api", "register"]
        }
      }
    },
    {
      "name": "Login",
      "request": {
        "method": "POST",
        "header": [
          { "key": "Content-Type", "value": "application/json" }
        ],
        "body": {
          "mode": "raw",
          "raw": "{\n  \"username\": \"your_username\",\n  \"password\": \"your_password\"\n}"
        },
        "url": {
          "raw": "{{base_url}}/api/login",
          "host": ["{{base_url}}"],
          "path": ["api", "login"]
        }
      }
    },
    {
      "name": "Forgot Password",
      "request": {
        "method": "POST",
        "header": [
          { "key": "Content-Type", "value": "application/json" }
        ],
        "body": {
          "mode": "raw",
          "raw": "{\n  \"email\": \"user@example.com\",\n  \"mailUser\": \"your_gmail_address@gmail.com\",\n  \"mailPass\": \"your_gmail_app_password\"\n}"
        },
        "url": {
          "raw": "{{base_url}}/api/forgot-password",
          "host": ["{{base_url}}"],
          "path": ["api", "forgot-password"]
        }
      }
    },
    {
      "name": "Reset Password",
      "request": {
        "method": "POST",
        "header": [
          { "key": "Content-Type", "value": "application/json" }
        ],
        "body": {
          "mode": "raw",
          "raw": "{\n  \"password\": \"your_new_password\"\n}"
        },
        "url": {
          "raw": "{{base_url}}/api/reset-password/:token",
          "host": ["{{base_url}}"],
          "path": ["api", "reset-password", ":token"]
        }
      },
      "event": [
        {
          "listen": "prerequest",
          "script": {
            "exec": [
              "// Replace :token in the URL with your actual reset token before sending."
            ]
          }
        }
      ]
    }
  ]
}
