import React from 'react';
import ImageSlider from './ImageSlider';

const HeroSection = () => {
  return (
    <section className="bg-black text-white  md:px-16 py-10 flex flex-col md:flex-row items-center justify-center gap-12 min-h-screen">
      {/* Left: Image Slider */}
      <ImageSlider />

      {/* Right: Content */}
      <div className="text-center md:text-left">
        <h1 className="text-2xl md:text-3xl font-semibold mb-6">
          America’s #1 Sweepstakes Experience
        </h1>
        <div className="flex flex-col md:flex-row items-center justify-center md:justify-start gap-4">
          <button className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded font-semibold">
            Register
          </button>
          <button className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded font-semibold">
            Deposit
          </button>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
