import React from 'react';

// Game platform images and optional badges
const platforms = [
  { title: 'Golden Dragon', image: '/src/assets/golden-dragon.png', badge: 'NEW!' },
  { title: 'Fire Kirin', image: '/src/assets/fire-kirin.png', badge: 'HOT!' },
  { title: 'Fortune King', image: '/src/assets/fortune-king.png' },
  { title: 'Fire Phoenix', image: '/src/assets/fire-phoenix.png' },
  { title: 'Juwa', image: '/src/assets/juwa.png' },
  { title: 'Orion Stars', image: '/src/assets/orion-stars.png' },
];

const SweepstakesPlatforms = () => {
  return (
    <section className="bg-black text-white py-16 px-4">
      {/* Title */}
      <div className="text-center mb-10">
        <h2 className="text-3xl md:text-4xl font-bold mb-2">SWEEPSTAKES PLATFORMS</h2>
        <p className="text-gray-300">Platforms have multiple games</p>
      </div>

      {/* Grid */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-8 max-w-7xl mx-auto">
        {platforms.map((platform, index) => (
          <div
            key={index}
            className="relative rounded-lg border-2 border-purple-900 overflow-hidden"
          >
            {/* Badge */}
            {platform.badge && (
              <div className="absolute top-2 left-2 bg-white text-red-600 font-bold text-xs px-2 py-1 rounded shadow">
                {platform.badge}
              </div>
            )}

            {/* Image */}
            <img
              src={platform.image}
              alt={platform.title}
              className="w-full h-48 object-cover hover:scale-105 transition-transform duration-300"
            />
          </div>
        ))}
      </div>
    </section>
  );
};

export default SweepstakesPlatforms;
