import React, { useState } from "react";
import { FaHome, FaRegCommentDots } from "react-icons/fa";
import { IoClose } from "react-icons/io5";
import { IoSendSharp } from "react-icons/io5";

const ChatSidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {/* Toggle Button */}
      {!isOpen && (
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded-full shadow-lg"
          onClick={() => setIsOpen(true)}
        >
          Chat
        </button>
      )}

      {/* Chat Panel */}
      {isOpen && (
        <div className="w-80 h-[500px] bg-[#1da1f2] rounded-lg shadow-2xl flex flex-col justify-between overflow-hidden">
          {/* Header */}
          <div className="flex justify-between items-center px-4 py-3">
            <p className="text-white font-semibold italic">Hi! How can we help you?</p>
            <button onClick={() => setIsOpen(false)} className="text-white text-lg">
              <IoClose />
            </button>
          </div>

          {/* Start Chat Card */}
          <div className="px-4">
            <div className="bg-white p-4 rounded-lg shadow flex justify-between items-center cursor-pointer hover:bg-gray-100 transition">
              <div>
                <p className="font-semibold">Start Chat</p>
                <p className="text-sm text-gray-500">We typically reply in a few minutes</p>
              </div>
              <IoSendSharp className="text-blue-500 text-xl" />
            </div>
          </div>

          {/* Spacer */}
          <div className="flex-grow"></div>

          {/* Bottom Navigation */}
          <div className="border-t bg-white flex justify-around items-center py-2">
            <FaHome className="text-blue-500 text-xl" />
            <FaRegCommentDots className="text-gray-700 text-xl" />
          </div>

          {/* Footer Text */}
          <div className="text-center text-sm text-gray-600 py-1 bg-white">
            Welcome to <span className="italic font-medium">Piggie Rich</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatSidebar;
