import React, { useState } from "react";

const GOOGLE_FORM_ACTION = "https://docs.google.com/forms/d/e/YOUR_REGISTER_FORM_ID/formResponse"; // Replace with your Google Form action URL

const RegisterPage = () => {
  const [submitted, setSubmitted] = useState(false);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form
        className="bg-white p-8 rounded shadow-md w-full max-w-md"
        action={GOOGLE_FORM_ACTION}
        method="POST"
        target="_blank"
        onSubmit={() => setSubmitted(true)}
      >
        <h2 className="text-2xl font-bold mb-6 text-center">Register</h2>
        <input name="entry.USERNAME_FIELD_ID" required placeholder="Username" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.PASSWORD_FIELD_ID" type="password" required placeholder="Password" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.CONFIRM_PASSWORD_FIELD_ID" type="password" required placeholder="Confirm Password" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.DOB_FIELD_ID" type="date" required placeholder="Date of Birth" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.FIRSTNAME_FIELD_ID" required placeholder="First Name" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.LASTNAME_FIELD_ID" required placeholder="Last Name" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.EMAIL_FIELD_ID" type="email" required placeholder="Email" className="mb-3 w-full border rounded px-3 py-2" />
        <input name="entry.PHONE_FIELD_ID" required placeholder="Phone" className="mb-6 w-full border rounded px-3 py-2" />
        <button type="submit" className="w-full bg-blue-500 text-white font-bold py-2 rounded hover:bg-blue-600">
          Register
        </button>
        {submitted && (
          <p className="text-green-600 mt-4 text-center">Registration submitted! Check your email.</p>
        )}
      </form>
    </div>
  );
};

export default RegisterPage;
